import React, {useState, useEffect} from 'react';
import { Button, Card, Image } from 'semantic-ui-react';

const DogPage = ( {dogID}) => {

  const number = dogID;
  const [dogInfo, setDogInfo] = useState({
    name: 'Bowie',
    owner: 'Blake',
    breed: 'Australian Cattle Dog',
    location: '1000 Paper St. Cincinnati OH 45000',
    age: 3,
    desc: 'This bundle of energy loves to go on walks!',
    payment: '$25',
    image: '',
    sex: 'male',
  });

  const [size, setSize] = useState({ size: 1, array: [0]});
  const numArray = [0];
  console.log(`Initial array: ${numArray}`);

  const generateDog = (e) => {
    const fetchData = async () => {
      // Generate random number
      let rand = 1;
      let includes = true;
      while (includes == true) {
        rand = Math.floor((Math.random() * 9) + 1);
        console.log(`The original array: ${rand}`);
        if (numArray.includes(rand) == false) {
          console.log(`This is not in the original array`);
          includes = false;
        }

      }
      numArray.push(rand);
      console.log(`Pushed array: ${numArray}`);
      const result = await fetch(`/api/dogs/${rand}`);

      const body = await result.json();
      setDogInfo(body);
    }
    fetchData();
  }


  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/api/dogs/${dogID}`);
      const body = await result.json();
      setDogInfo(body);
    }
    fetchData();
  }, []);

  return (
    <>
    <Card>
      <Card.Content>
        <Image />
        <Card.Header>{dogInfo.name}</Card.Header>
        <Card.Meta>Breed: {dogInfo.breed} Age: {dogInfo.age} Sex: {dogInfo.sex}</Card.Meta>
        <Card.Description>{dogInfo.desc}</Card.Description>
      </Card.Content>
      <Card.Content extra>
        <Card.Description>Location: {dogInfo.location} Payment: {dogInfo.payment} </Card.Description>
      </Card.Content>
    </Card>
    <Button primary onClick={generateDog}>Skip</Button>
    <Button secondary>Message</Button>
  </>
  );
}

export default DogPage;
