import React, {useState, useEffect} from 'react';
import { Button, Card, Image } from 'semantic-ui-react';

const DogPage = ( {dogID}) => {

  const number = dogID;
  const [dogInfo, setDogInfo] = useState({
    name: 'Bowie',
    owner: 'Blake',
    breed: 'Australian Cattle Dog',
    location: '1000 Paper St. Cincinnati OH 45000',
    age: 3,
    desc: 'This bundle of energy loves to go on walks!',
    payment: '$25',
    image: '',
    sex: 'male',
  });

  const [size, setSize] = useState({ size: 1});

  useEffect(() => {

    const fetchData = async () => {

      const result = await fetch(`/api/dogs/${num}`);
      const body = await result.json();
      setDogInfo(body);
    }
    fetchData();
  }, [num]);


  return (
    <>
    <Card>
      <Card.Content>
        <Image />
        <Card.Header>{dogInfo.name}</Card.Header>
        <Card.Meta>Breed: {dogInfo.breed} Age: {dogInfo.age} Sex: {dogInfo.sex}</Card.Meta>
        <Card.Description>{dogInfo.desc}</Card.Description>
      </Card.Content>
      <Card.Content extra>
        <Card.Description>Location: {dogInfo.location} Payment: {dogInfo.payment} </Card.Description>
      </Card.Content>
    </Card>
  </>
  );
}

export default DogPage;
